import React, { useState } from "react";

export default function Textform(props) {
	const handleUpClick = () => {
		let newText = text.toUpperCase();
		setText(newText);
	};
	const handleOnChange = (event) => {
		setText(event.target.value);
	};

	const clearText = () => {
		let newText = "";
		setText(newText);
	};
	const handleLowerClick = () => {
		let newText = text.toLowerCase();
		setText(newText);
	};
	const [text, setText] = useState("");
	return (
		<>
			<div className="container">
				<h1>{props.heading}</h1>
				<div className="mb-3">
					<textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
				</div>
				<button type="button" className="btn btn-primary mx-1" onClick={handleUpClick}>
					Upper Case
				</button>
				<button type="button" class="btn btn-primary " onClick={handleLowerClick}>
					{" "}
					Lower Case{" "}
				</button>
				<button type="button" className="btn btn-primary mx-1" onClick={clearText}>
					Clear Text
				</button>
				<button type="button" className="btn btn-primary mx-1" onClick={clearText}>
					Clear Text
				</button>
			</div>
			<div className="container my-3">
				<h2>Text Summary</h2>
				<p>
					Your text contains {text.split(" ").length} words and {text.length} characters
				</p>
				<p>{text.split(" ").length * 0.008} Minutes read</p>
				<h2>Preview</h2>
				<p>{text}</p>
			</div>
		</>
	);
}
